mapper.select:
select distinct * from tb_menu m inner join tb_role_menu rm on rm.menuId=m.id where m.parentId is null and rm.roleId in
#{role.name}
order by priority desc

select distinct * from tb_menu m inner join tb_role_menu rm on rm.menuId=m.id where m.parentId = #{pid} and rm.roleId in
#{role.name}
order by priority desc
